import React from 'react';
import ImageCarousel from './ImageCarousel';
import { useParams } from 'react-router-dom';
import { useSelector } from "react-redux";
import { createChats } from '../actions/chatActions';
import { useNavigate } from "react-router-dom";

export default function CardDetails() {
    const history = useNavigate();
    const chatData ={
        senderId:"",
        receiverId:"" 
    };

    const { id } = useParams();
    const items = useSelector((state) => state.items);
    const item = items.find((item) => item._id.toString() === id);
    const user = useSelector((state) => state.auth.authData.result);

    const handleMessage = async(e) => {
        e.preventDefault();
        chatData.senderId = user._id;
        chatData.receiverId = item.userId;
        await createChats(chatData);
        history('/messages');
    };

    return (
        <>
            <div className="min-h-screen bg-gray-100 text-gray-900 flex justify-center p-2">
                <div className="max-w-screen-xl m-0 sm:m-10 bg-white shadow sm:rounded-lg flex flex-col lg:flex-row justify-center flex-1">
                    {/* Images for smaller screens */}
                    <div className="flex-1 bg-indigo-100 text-center lg:hidden">
                        <ImageCarousel slides={item.images} />
                    </div>

                    <div className="lg:w-1/2 xl:w-5/12 p-4 sm:p-12 flex flex-col items-center">
                        <div className="mt-12 flex flex-col items-center">
                            {/* Images for larger screens */}
                            <div className="hidden lg:block flex-1 bg-indigo-100 text-center">
                                <ImageCarousel slides={item.images} />
                            </div>

                            <h1 className="text-2xs xl:text-3xl font-extrabold">
                                {item.category}
                            </h1>
                            <div className="flex flex-col items-center"> 
                                <div className="my-12 border-b text-center">
                                    <div className="leading-none px-2 inline-block text-sm text-gray-600 tracking-wide font-medium bg-white transform translate-y-1/2">
                                        {item.details}
                                    </div>
                                </div>
                            </div>
                            <div className="w-full mt-8">                                
                                <div className="mx-auto max-w-xs">
                                    <button 
                                        onClick={handleMessage}
                                        className="mt-5 tracking-wide font-semibold bg-indigo-500 text-gray-100 w-full py-4 rounded-lg hover:bg-indigo-700 transition-all duration-300 ease-in-out flex items-center justify-center focus:shadow-outline focus:outline-none"
                                    >
                                        <svg className="w-6 h-6 -ml-2" fill="none" stroke="currentColor" strokeWidth="2"
                                            strokeLinecap="round" strokeLinejoin="round">
                                            <path d="M16 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
                                            <circle cx="8.5" cy="7" r="4" />
                                            <path d="M20 8v6M23 11h-6" />
                                        </svg>
                                        <span className="ml-3">
                                            Message
                                        </span>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
