import React, { useState, useEffect, useSyncExternalStore, useRef } from 'react';
import ChatList from './ChatList';
import { userChats } from '../actions/chatActions';
import Chatbox from './ChatBox';
import { io } from "socket.io-client";

export default function ChatLayout() {
  const user = JSON.parse(localStorage.getItem("profile-LostAndFound"));
  const [currentChatList, setCurrentChatList] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [sendMessage, setSendMessage] = useState(null);
  const [receivedMessage, setReceivedMessage] = useState(null);
  const socket = useRef();

  useEffect(() => {
    socket.current =io('http://localhost:8800')
    socket.current.emit("new-user-add", user.data.result._id);
    socket.current.on('get-users',(users)=>{
      setOnlineUsers(users);

    })
  }, [user])


  useEffect(() => {
    getChatList();
  }, []);


  // Send Message to socket server
  useEffect(() => {
    if (sendMessage!==null) {
      socket.current.emit("send-message", sendMessage);}
  }, [sendMessage]);


  // Get the message from socket server
  useEffect(() => {
    socket.current.on("recieve-message", (data) => {
      console.log(data)
      setReceivedMessage(data);
    }

    );
  }, []);

  const getChatList = async () => {
    try {
      const chatListData = await userChats(user.data.result._id);
      setChatList(chatListData);
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <>
      <div className="flex h-screen antialiased text-gray-800">
        <div className="flex flex-row h-full w-full overflow-x-hidden">
          <div className="flex flex-col py-8 pl-6 pr-2 w-64 bg-white flex-shrink-0">
            <div className="flex flex-row items-center justify-center h-12 w-full">
              <div className="flex items-center justify-center rounded-2xl text-indigo-700 bg-indigo-100 h-10 w-10">
                <svg
                  className="w-6 h-6"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z"
                  ></path>
                </svg>
              </div>
              <div className="ml-2 font-bold text-2xl">Your Chats</div>
            </div>
            <div className="flex flex-col items-center bg-indigo-100 border border-gray-200 mt-4 w-full py-6 px-4 rounded-lg">
              <div className="h-20 w-20 rounded-full border overflow-hidden">
                <img
                  src="https://avatars3.githubusercontent.com/u/2763884?s=128"
                  alt="Avatar"
                  className="h-full w-full"
                />
              </div>
              <div className="text-sm font-semibold mt-2">Aminos Co.</div>
              <div className="flex flex-row items-center mt-3">
                <div className="flex flex-col justify-center h-4 w-8 bg-indigo-500 rounded-full">
                  <div className="h-3 w-3 bg-white rounded-full self-end mr-1"></div>
                </div>
                <div className="leading-none ml-1 text-xs">Active</div>
              </div>
            </div>
            {/* chatList */}
            {chatList && chatList.map((chat) => (
              <div onClick={()=>setCurrentChatList(chat)}>
              <ChatList key={chat._id} data={chat} loggedInUser={user.data.result._id} />
              </div>
            ))}
          </div>

            {/* Chatbox */}
          <Chatbox chat={currentChatList} loggedInUser={user.data.result._id} setSendMessage={setSendMessage}
          receivedMessage={receivedMessage}></Chatbox>
        </div>
      </div>
    </>
  );
}
