import React, { useState, useEffect, useRef } from 'react';
import { getMessages, addMessage } from '../actions/chatActions';
import { format } from "timeago.js";

export default function Chatbox({ chat, loggedInUser, setSendMessage, receivedMessage }) {

  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const scroll = useRef();

  useEffect(() => {

    const fetchMessages = async () => {
      try {
        const data = await getMessages(chat._id);
        setMessages(data);
      } catch (error) {
        console.log(error);
      }
    };

    if (chat !== null) fetchMessages();
  }, [chat]);

  const handleChange = (e) => {
    setNewMessage(e.target.value);
  }

  const handleClick = async (e) => {
    e.preventDefault();

    const textMessage = {
      senderId: loggedInUser,
      text: newMessage,
      chatId: chat._id,
    }

    const receiverId = chat.members.find((id) => id !== loggedInUser);

    setSendMessage({ ...textMessage, receiverId });

    try {
      const data = await addMessage(textMessage);
      setMessages([...messages, data]);
      setNewMessage("");
    } catch {
      console.log("error");
    }
  }

  useEffect(() => {
    if (receivedMessage != null && receivedMessage.chatId === chat._id) {
      setMessages([...messages, receivedMessage]);
    }
  }, [receivedMessage]);

  useEffect(() => {
    scroll.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <>
      {chat ? (
        // <div className="flex flex-col flex-auto h-full p-6 lg:sticky top-0">
